'use strict';

var count = 0;

function addToDo() {

    var todo = document.getElementById('input'),
        node = document.createElement('li');

    node.innerHTML = '<input type="checkbox" /><span>' + todo.value + '</span>';

    document.getElementById('column' + (count % 2 + 1)).appendChild(node);

    todo.value = '';
    count++;
}
